#!/usr/bin/env node

import { McpServer } from "@modelcontextprotocol/sdk/server/mcp.js";
import { StdioServerTransport } from "@modelcontextprotocol/sdk/server/stdio.js";
import { z } from "zod";

// Create a new MCP server with stdio transport
const server = new McpServer(
  {
    name: "image-server",
    version: "1.0.0",
    capabilities: {
      tools: {
        "generate_image": {
          description: "A tool that returns a red circle image",
          parameters: {
            type: "object",
            properties: {},
            required: []
          }
        }
      }
    }
  }
);

// Replace this placeholder with your actual base64 encoded red circle image
const RED_CIRCLE_BASE64 = "data:image/png;base64,";

// Register the generate_image tool
server.tool("generate_image", async (params) => {
  return {
    content: [{
      type: "image",
      base64: RED_CIRCLE_BASE64
    }]
  };
});

// Connect to the transport and start the server
await server.connect(new StdioServerTransport()); 